mapper.select:
SELECT uva.c AS "totalNum", uvb.c AS "firstChoiceNum" FROM ( SELECT COUNT( 1 ) c FROM user_volunteer uv WHERE uv.is_deleted != 1 AND uv.admission_id = #{admissionId,jdbcType=INTEGER}
AND uv.department_id = #{departmentId,jdbcType=INTEGER}
GROUP BY uv.admission_id ) AS uva, ( SELECT COUNT( 1 ) c FROM user_volunteer uv WHERE uv.is_deleted != 1 AND uv.admission_id = #{admissionId,jdbcType=INTEGER}
AND uv.department_id = #{departmentId,jdbcType=INTEGER}
AND uv.department_order = 1 AND uv.organization_order = 1 GROUP BY uv.admission_id ) AS uvb
