mapper.select:
SELECT ui.student_id AS 'studentId', ui.`name` AS 'name', i.sign_in_time AS 'time', aa.`name` AS 'room' FROM interview_status i LEFT JOIN user_info ui ON i.user_id = ui.id LEFT JOIN admission_address aa ON i.admission_address_id = aa.id WHERE i.is_deleted != 1 AND aa.is_deleted != 1 AND i.round = #{round,jdbcType=INTEGER}
AND i.department_id = #{departmentId,jdbcType=INTEGER}
AND i.state >= 5 AND i.admission_id = #{admissionId,jdbcType=INTEGER}

SELECT zml.room AS "room", zml.total AS "total", ml.finished AS "finished" FROM ( SELECT COUNT( 1 ) AS "total", aa.`name` AS 'room' FROM interview_status i LEFT JOIN user_info ui ON i.user_id = ui.id LEFT JOIN admission_address aa ON i.admission_address_id = aa.id WHERE i.is_deleted != 1 AND aa.is_deleted != 1 AND i.round = #{round,jdbcType=INTEGER}
AND i.department_id = #{departmentId,jdbcType=INTEGER}
AND i.state >= 5 AND i.admission_id = #{admissionId,jdbcType=INTEGER} GROUP BY aa.`name` ) AS `zml` LEFT JOIN ( SELECT COUNT( 1 ) AS "finished", aa.`name` AS 'room' FROM interview_status i LEFT JOIN user_info ui ON i.user_id = ui.id LEFT JOIN admission_address aa ON i.admission_address_id = aa.id WHERE i.is_deleted != 1 AND i.round = #{round,jdbcType=INTEGER}
AND i.department_id = #{departmentId,jdbcType=INTEGER}
AND i.state >= 6 AND i.admission_id = #{admissionId,jdbcType=INTEGER} GROUP BY aa.`name` ) AS `ml` ON zml.room = ml.room
