mapper.update:
update user_info
set

    phone = #{phoneNum},


    email = #{email},


    qq = #{qqNum}

where student_id = #{studentId}

mapper.select:
select id as userId
from user_info
where student_id = #{studentId,jdbcType=INTEGER}

select is_certification as is_certification
from user_info
where id = #{userId,jdbcType=INTEGER}
